mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY COUPON_HISTORY_ID DESC) AS RN ,h.* ,d.COUPON_PRICE ,i.MEMBER_ID AS SELLER_ID ,c.ITEM_NAME ,c.ITEM_PRICE FROM COUPON_HISTORY h LEFT JOIN DISCOUNT_COUPON d ON h.COUPON_ID = d.COUPON_ID LEFT JOIN ITEM i ON h.ITEM_ID = i.ITEM_ID LEFT JOIN ITEM_CTG c ON h.ITEM_ID = c.ITEM_ID
WHERE ITEM_NAME LIKE '%'||#{pagination.keyword}||'%'
) WHERE RN BETWEEN #{pagination.start} AND #{pagination.end}
