mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ITEM_ID DESC) AS RN, ITEM_CTG.* FROM ITEM_CTG WHERE 1=1 AND ITEM_STATUS = 100
AND ITEM_NAME LIKE '%'||#{pagination.keyword}||'%'
AND MAIN_CTG LIKE '%'||#{pagination.keyword}||'%'
AND SUB_CTG LIKE '%'||#{pagination.keyword}||'%'
) WHERE RN BETWEEN #{pagination.start} AND #{pagination.end}

SELECT * FROM
(
SELECT ROW_NUMBER() OVER(ORDER BY ITEM_ID DESC) AS RN, ITEM_CTG.*
FROM
ITEM_CTG
WHERE 1=1 AND SUB_CTG = #{subCtg} AND ITEM_STATUS = 100 AND NOT ITEM_ID = #{itemId}
)
WHERE RN BETWEEN #{pagination.start} AND #{pagination.end}

mapper.update:
UPDATE ITEM SET
ITEM_NAME = #{itemName},
ITEM_CONTENT = #{itemContent}
WHERE ITEM_ID = #{itemId}

UPDATE ITEM_CTG
SET ITEM_AMOUNT = #{itemAmount}
WHERE
ITEM_ID = #{itemId}

UPDATE ITEM_CTG
SET ITEM_STATUS = #{itemStatus}
WHERE
ITEM_ID = #{itemId}
