mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ITEM_ID DESC) AS RN, ITEM_CTG.* FROM ITEM_CTG
WHERE ITEM_NAME LIKE '%'||#{pagination.keyword}||'%'
WHERE ITEM_NAME LIKE '%'||#{pagination.keyword}||'%'
WHERE MAIN_CTG LIKE '%'||#{pagination.keyword}||'%'
) WHERE RN BETWEEN #{pagination.start} AND #{pagination.end}


SELECT * FROM
(
SELECT ROW_NUMBER() OVER(ORDER BY ITEM_ID DESC) AS RN, ITEM_CTG.*
FROM
ITEM_CTG
WHERE 1=1

	AND MEMBER_ID = #{memberId}


	AND ITEM_STATUS = #{itemStatus}

)
WHERE RN BETWEEN #{pagination.start} AND #{pagination.end}

mapper.update:
UPDATE ITEM
SET ITEM_AMOUNT = #{itemAmount}
WHERE
ITEM_ID = #{itemId}

UPDATE ITEM_CTG
SET ITEM_STATUS = #{itemStatus}
WHERE
ITEM_ID = #{itemId}
