mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY a.ITEM_ID DESC) AS RN, b.* FROM ITEM a LEFT JOIN ITEM_CTG b ON a.ITEM_ID = b.ITEM_ID WHERE 1=1 AND ITEM_STATUS = 100
AND b.ITEM_NAME LIKE '%'||#{keyword}||'%'
AND b.MAIN_CTG LIKE '%'||#{keyword}||'%'
AND b.SUB_CTG LIKE '%'||#{keyword}||'%'
AND a.ITEM_CONTENT LIKE '%'||#{keyword}||'%'
) WHERE RN BETWEEN #{pagination.start} AND #{pagination.end}

SELECT * FROM
(
SELECT ROW_NUMBER() OVER(ORDER BY ITEM_ID DESC) AS RN, ITEM_CTG.*
FROM
ITEM_CTG
WHERE 1=1 AND SUB_CTG = #{subCtg} AND ITEM_STATUS = 100 AND NOT ITEM_ID = #{itemId}
)
WHERE RN BETWEEN #{pagination.start} AND #{pagination.end}

mapper.update:
UPDATE ITEM SET
ITEM_NAME = #{itemName},
ITEM_CONTENT = #{itemContent}
WHERE ITEM_ID = #{itemId}

UPDATE ITEM_CTG
SET ITEM_AMOUNT = #{itemAmount}
WHERE
ITEM_ID = #{itemId}

update.foreach:
UPDATE ITEM_CTG
SET ITEM_AMOUNT = ITEM_AMOUNT + #{order.itemAmount}
WHERE
ITEM_ID = #{order.itemId}

mapper.update:
UPDATE ITEM_CTG
SET ITEM_STATUS = #{itemStatus}
WHERE
ITEM_ID = #{itemId}
