mapper.select:
SELECT COUPON_ACTIVE.COUPON_ACTIVE_ID ,COUPON_ACTIVE.MEMBER_ID ,COUPON_ACTIVE.COUPON_ID ,COUPON_ACTIVE.COUPON_NAME ,COUPON_ACTIVE.COUPON_ISSUED_DATE ,COUPON_ACTIVE.COUPON_EXPIRE_DATE ,COUPON_ACTIVE.COUPON_USE_DATE ,COUPON_ACTIVE.ITEM_ID ,COUPON_ACTIVE.ITEM_NAME ,DISCOUNT_COUPON.COUPON_PRICE ,DISCOUNT_COUPON.COUPON_USE_CONDITION FROM COUPON_ACTIVE LEFT JOIN DISCOUNT_COUPON ON COUPON_ACTIVE.COUPON_ID = DISCOUNT_COUPON.COUPON_ID WHERE 1=1
AND COUPON_ACTIVE.MEMBER_ID = #{memberId}
AND (COUPON_ACTIVE.ITEM_ID = #{itemId} OR COUPON_ACTIVE.ITEM_ID = 0)

mapper.update:
UPDATE COUPON_ACTIVE
SET COUPON_USE_DATE = #{couponUseDate}
,ITEM_ID = #{itemId}
,ORDER_ID = #{orderId}
WHERE COUPON_ACTIVE_ID = #{couponActiveId}
